import './App.css';
import { useState } from 'react'




function App() {

  const [inputsValue, setInputsValue] = useState({
    endpoint: ' ',
    idInp: ' ',
  })

  const controlInputs = (nameOfInput, value) => {
    setInputsValue({ [nameOfInput]: value })
  }

  console.log(inputsValue.endpoint);
  return (
    <>
      <input value={inputsValue.endpoint}
        onChange={({ target: { value } }) => (setInputsValue('endpoint', value))}
        type="text" name="endpoint" placeholder='Enter endpoint e.g "posts"' />

    </>
  )
}



export default App;
